"use strict";var TAR={init:function(){this.scrollAnimation(),this.toggleNav()},scrollAnimation:function(){$("a[href*=#]:not([href=#])").click(function(a){if(a.preventDefault(),location.pathname.replace(/^\//,"")===this.pathname.replace(/^\//,"")&&location.hostname===this.hostname){var b=this.hash.slice(1),c=document.getElementById(b);return $(".content").animate({scrollTop:c.offsetTop},1e3),!1}})},toggleNav:function(){$("#menu").click(function(a){a.preventDefault(),$(".navigation__list").toggleClass("active")})}};$(function(){TAR.init()});